import React, { useEffect, useState } from 'react'
import { firestore } from '../../../firebase/config'
export default function Userleave(props) {

    const [typeLeave, setTypeLeave] = useState([])
    const refTypeLeave = firestore.collection("type_leave")
    useEffect(() => {

        refTypeLeave.onSnapshot(type_leave => {
            let tempDataArray = []
            type_leave.forEach(data => {
                tempDataArray = [
                    ...tempDataArray,
                    {
                        type_id:data.id,
                        type_leave_name: data.data().type_leave_name
                    }
                ]
                setTypeLeave(tempDataArray)
            })
        })
    })

    const test =typeLeave.find(data=>
      (  data.type_id===props.data.type_leave_id
    ))

    const test2=test.type_id
    return (
        <tr>
            <td onClick={()=>{console.log(test)}}>
            {
               test2
            }
            </td>
            <td>{props.data.leave_start}</td>
            <td>{props.data.amount}</td>
            <td>
                <span className="badge badge-pill badge-dark">รออนุมัติ</span>
            </td>
            <td>
                <button className="btn btn-sm btn-outline-danger">การอนุมัติ</button>
            </td>
        </tr>

    )
}
