import React, { useEffect, useState } from 'react';
import Footer from '../../component/admin/Footer';
import Navbar from '../../component/admin/Navbar';
import Sidebar from '../../component/admin/Sidebar';
import Checkadmin from './Checkadmin';
import Form from './Employee/Form';
import { firestore } from '../../firebase/config'
import {MDBDataTableV5} from 'mdbreact'
import axios from "axios"
function Employee() {
  const [user,setUser]=useState([])
  const refUser = firestore.collection("user");
  const [loading, setLoading] = useState(true)
  useEffect(() => {
    const unsubscribe = refUser.onSnapshot(snapshot => {
      let tempDataArray = [];
      snapshot.forEach(doc => {
        tempDataArray = [
          ...tempDataArray,
          {
            information: doc.data().email+doc.data().email,
            name: doc.data().fname +" "+ doc.data().lname+" ("+doc.data().name+")",
            uid: doc.data().uid,
            user_group: doc.data().user_group
          }
        ];
      })
      setUser(tempDataArray)
      setLoading(false)
    })
    return () => {
    };
  });


  const deleteHandle = () => {

  }
  const [rows,setRows]=useState({

  })
  const [datatable, setDatatable] = React.useState({
    columns: [
      {
        label: 'ชื่อพนักงาน',
        field: 'name',
        width: 150,
        attributes: {
          'aria-controls': 'DataTable',
          'aria-label': 'Name',
        },
      },
      {
        label: 'ข้อมูลส่วนตัว',
        field: 'information',
        width: 270,
      },
      {
        label: 'Phone',
        field: 'phone',
        width: 200,
      },
    ],

  });

  const test =()=>{
    setDatatable({...datatable,rows:[...user]})

    console.log(datatable)
  }
  return (
    <div>
      <Checkadmin />
      <Navbar />
      <Sidebar />
      {/* Content Wrapper. Contains page content */}
      <div className="content-wrapper">
        {/* Content Header (Page header) */}
        <div className="content-header">
          <div className="container-fluid">
            <div className="row mb-2">
            </div>{/* /.row */}
          </div>{/* /.container-fluid */}
        </div>
        {/* /.content-header */}
        {/* Main content */}
        <div className="content">
          <div className="container-fluid">
            <div className="row">
              <div className="col-lg-12">
                <div className="card">
                  <div className="card-header ">
                    <div className="d-flex justify-content-between">
                      <h3 className="m-0 text-dark" onClick={()=>{console.log(test())}}>พนักงาน</h3>
                      <Form />
                    </div>
                  </div>
                  <div className="card-body table-responsive ">
                  <MDBDataTableV5 hover entriesOptions={[5, 20, 25]} entries={5} pagesAmount={4} data={datatable,""} searchTop searchBottom={false} />
                  </div>
                </div>
              </div>
              {/* /.col-md-6 */}
              {/* /.col-md-6 */}
            </div>
            {/* /.row */}
          </div>
          {/* /.container-fluid */}
        </div>
        {/* /.content */}
      </div>
      {/* /.content-wrapper */}
      {/* Control Sidebar */}
      <aside className="control-sidebar control-sidebar-dark">
        {/* Control sidebar content goes here */}
      </aside>
      {/* /.control-sidebar */}
      <Footer />
    </div>
  );
}

export default Employee;
