import React, { useEffect, useState } from 'react';
import Footer from '../../component/admin/Footer';
import Navbar from '../../component/admin/Navbar';
import Sidebar from '../../component/admin/Sidebar';
import Checkadmin from './Checkadmin';
import Form from './Employee/Form';
import Userlist from './Employee/Employee'
import { firestore } from '../../firebase/config'
import axios from 'axios'
import $  from 'jquery'
import { MDBDataTableV5,MDBBtn } from 'mdbreact';

function Employee() {
  const ref = firestore.collection("user");
  const [user, setUser] = useState([])
  const [loading, setLoading] = useState(true)

  const [datatable, setDatatable] = useState({
    columns: [
      {
        label: 'ชื่อพนักงาน',
        field: 'name',
        width: 150,
        attributes: {
          'aria-controls': 'DataTable',
          'aria-label': 'Name',
        },
      },
      {
        label: 'ชื่อเล่น',
        field: 'position',
        width: 270,
      },
      {
        label: 'ข้อมูลส่วนตัว',
        field: 'office',
        width: 200,
      },
      {
        label: 'จัดการ',
        field: 'age',
        sort: 'asc',
        width: 100,
      },
      {
        label: 'Start date',
        field: 'date',
        sort: 'disabled',
        width: 150,
      },
      {
        label: 'Salary',
        field: 'salary',
        sort: 'disabled',
        width: 100,
      },
    ],
    rows: [
      {
        name: 'Tiger Nixon',
        position: 'System Architect',
        office: 'Edinburgh',
        age: '61',
        date: '2011/04/25',
        salary: <button className="btn btn-sm btn-danger">safasfj</button>,
      },
      {
        name: 'Garrett Winters',
        position: 'Accountant',
        office: 'Tokyo',
        age: '63',
        date: '2011/07/25',
        salary: '$170',
      },
      {
        name: 'Ashton Cox',
        position: 'Junior Technical Author',
        office: 'San Francisco',
        age: '66',
        date: '2009/01/12',
        salary: '$86',
      },
      {
        name: 'Cedric Kelly',
        position: 'Senior Javascript Developer',
        office: 'Edinburgh',
        age: '22',
        date: '2012/03/29',
        salary: '$433',
      },
      {
        name: 'Airi Satou',
        position: 'Accountant',
        office: 'Tokyo',
        age: '33',
        date: '2008/11/28',
        salary: '$162',
      },
      {
        name: 'Brielle Williamson',
        position: 'Integration Specialist',
        office: 'New York',
        age: '61',
        date: '2012/12/02',
        salary: '$372',
      },
      {
        name: 'Herrod Chandler',
        position: 'Sales Assistant',
        office: 'San Francisco',
        age: '59',
        date: '2012/08/06',
        salary: '$137',
      },
      {
        name: 'Rhona Davidson',
        position: 'Integration Specialist',
        office: 'Tokyo',
        age: '55',
        date: '2010/10/14',
        salary: '$327',
      },
      {
        name: 'Colleen Hurst',
        position: 'Javascript Developer',
        office: 'San Francisco',
        age: '39',
        date: '2009/09/15',
        salary: '$205',
      },
      {
        name: 'Sonya Frost',
        position: 'Software Engineer',
        office: 'Edinburgh',
        age: '23',
        date: '2008/12/13',
        salary: '$103',
      },
      {
        name: 'Jena Gaines',
        position: 'Office Manager',
        office: 'London',
        age: '30',
        date: '2008/12/19',
        salary: '$90',
      },
      {
        name: 'Quinn Flynn',
        position: 'Support Lead',
        office: 'Edinburgh',
        age: '22',
        date: '2013/03/03',
        salary: '$342',
      }
    ],
  });

  useEffect(() => {


    const unsubscribe = ref.onSnapshot(snapshot => {
      let tempDataArray = [];
      snapshot.forEach(doc => {
        tempDataArray = [
          ...tempDataArray,
          {
            email: doc.data().email,
            fname: doc.data().fname,
            lname: doc.data().lname,
            name: doc.data().name,
            phone: doc.data().phone,
            uid: doc.id,
            user_group: doc.data().user_group
          }
        ];
      })
      setUser(()=>tempDataArray)
      setLoading(false)
    })
    return () => {
    };
  });


  const deleteHandle = (uid) => {
    let obj={
      uid
    }

    axios.post("http://localhost:4000/deleteuser",obj)
    .then(async (uid)=>{
      const refUser = firestore.collection("user").doc(uid.data)
      const refQuota = firestore.collection("quota").where(uid,"==",uid.data)
      await refUser.delete()
      refQuota.get().delete().then(()=>{
        console.log("delete success")
      })
    })
  }


  const test = ()=>{

  }
  return (
    <div>
      <Checkadmin />
      <Navbar />
      <Sidebar />
      {/* Content Wrapper. Contains page content */}
      <div className="content-wrapper">
        {/* Content Header (Page header) */}
        <div className="content-header">
          <div className="container-fluid">
            <div className="row mb-2">
            </div>{/* /.row */}
          </div>{/* /.container-fluid */}
        </div>
        {/* /.content-header */}
        {/* Main content */}
        <div className="content">
          <div className="container-fluid">
            <div className="row">
              <div className="col-lg-12">
                <div className="card">
                  <div className="card-header ">
                    <div className="d-flex justify-content-between">
                      <h3 className="m-0 text-dark" onClick={()=>console.log(user)}>พนักงาน</h3>
                      <Form />
                    </div>
                  </div>
                  <div className="card-body table-responsive ">
                  <MDBDataTableV5 hover entriesOptions={[5, 20, 25]} entries={5} pagesAmount={4} data={datatable} materialSearch />;
                    <table id="myTable" className="table table-hover ">
                      <thead className="thead-dark ">
                        <tr>
                          <th  class="test"  scope="col">ชื่อพนักงาน</th>
                          <th scope="col">ชื่อเล่น</th>
                          <th scope="col">ข้อมูลส่วนตัว</th>
                          <th scope="col">จัดการ</th>
                        </tr>
                      </thead>
                      <tbody>
                        {
                          loading ? (
                            <tr>
                              <td></td>
                              <td></td>
                              <td><div className="spinner-border mx-auto" style={{width:"3rem",height:"3rem"}} role="status">
                              </div></td>
                            </tr>
                          ) : null
                        }
                        {
                          user.map((item, index) => (
                            <Userlist key={index} data={item} delete={() => deleteHandle(item.uid)} />
                          ))
                        }
                      </tbody>
                    </table>
                  </div>
                </div>
              </div>
              {/* /.col-md-6 */}
              {/* /.col-md-6 */}
            </div>
            {/* /.row */}
          </div>
          {/* /.container-fluid */}
        </div>
        {/* /.content */}
      </div>
      {/* /.content-wrapper */}
      {/* Control Sidebar */}
      <aside className="control-sidebar control-sidebar-dark">
        {/* Control sidebar content goes here */}
      </aside>
      {/* /.control-sidebar */}
      <Footer />
    </div>
  );
}

export default Employee;

$(document).ready(()=>{
  $(".test").click(()=>{

    alert("ddd")
  })
})
