import React, { useEffect, useState } from 'react';
import {auth, firestore } from '../../../firebase/config'

const Form = () => {
    const [data,setData]=useState({
        leave_id:"",
        leave_date:"",
        leave_start:"",
        reson:"",
        amount:"",
        type_leave_id:"",
        uid:""
    })
    const [user,setUser]=useState([])
    const [typeLeave,setTypeLeave]=useState([])
    const refTypeLeave = firestore.collection("type_leave")
    const refUser= firestore.collection("user")
    useEffect(()=>{

        // info user
        const authUnsubscribe = auth.onAuthStateChanged((firebaseUser)=>{
            if(!!firebaseUser){
                refUser.doc(firebaseUser.uid).onSnapshot(doc=>{
                    setUser({...doc.data(),id:doc.id})
                })
            }
        })

        // find TypeLeave all
        const typeleavesubscribe = refTypeLeave.onSnapshot(doc=>{
            let tempArrayTypeLeave =[]
            doc.forEach(data=>{
                tempArrayTypeLeave=[
                    ...tempArrayTypeLeave,
                    {
                        type_leave_id:data.id,
                        type_leave_name:data.data().type_leave_name
                    }

                ]
            })
            setTypeLeave(tempArrayTypeLeave)
        })



    },[])
    return (
        <div>
            <div className="dropdown">
                <button onClick={()=>{console.log(user)}} className="btn btn-sm btn-info dropdown-toggle mr-4" type="button" id="dropdownMenuButton" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false" >
                    ยื่นเรื่องขอลา
                        </button>
                <div className="dropdown-menu" aria-labelledby="dropdownMenuButton">
                            {
                                typeLeave.map((data,index)=>{
                                    return <div key={index} className="dropdown-item" onClick={(e)=>{setData({...data,type_leave_id:e.target.id})}} id={data.type_leave_id} data-toggle="modal" data-target="#staticBackdrop" >{data.type_leave_name}</div>    
                                })
                            }       
                </div>
            </div>
            <div className="modal fade" id="staticBackdrop" data-backdrop="static" data-keyboard="false" tabIndex="-1" aria-labelledby="staticBackdropLabel" aria-hidden="true">
                <div className="modal-dialog">
                    <div className="modal-content">
                        <div className="modal-header">
                            <h5 className="modal-title" id="exampleModalLabel"></h5>
                            <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                                <span aria-hidden="true">×</span>
                            </button>
                        </div>
                        <div className="modal-body">
                            <div className="container-fluid">
                                <div className="row">
                                    <div className="col-md-6">
                                        <label onClick={()=>{console.log(data)}}>วันที่ลา</label>
                                        <input type="date" className="form-control" />
                                    </div>
                                    <div className="col-md-6">
                                        <label htmlFor="">จำนวนการลา</label>
                                        <input type="number" min={0} max={8} className="form-control" />

                                    </div>
                                </div>
                                <div className="row">
                                    <div className="col-md-12">
                                        <div className="form-group">
                                            <label htmlFor="" >เหตุผล</label>
                                            <textarea className="form-control" ></textarea>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                        <div className="modal-footer">
                            <button type="button" className="btn btn-secondary" data-dismiss="modal" >Close</button>
                            <button type="button" className="btn btn-primary" data-dismiss="modal" >Save changes</button>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Form;
