import React, { useEffect, useState } from 'react';
import {auth, firestore } from '../../../firebase/config'
const Form = () => {
    const [data, setData] = useState({ // all data
        type_leave: "",
        id_type_leave: "",
        timestart: "",
        timeend: "",   //amount Leave
        reason: "",
    })
    const [amount,setAmount]=useState();
    const [user,setUser]=useState(""); //Id User
    const [typeleave, setTypeleave] = useState([])  //TypeLeave
    const [amount2,setAmout2]=useState();
    useEffect(() => {

        const ref_type_leave = firestore.collection("type_leave");
        const unsubscribe = ref_type_leave.onSnapshot(snapshot => {
            let tempDataArry = []
            snapshot.forEach(data => {
                tempDataArry = [
                    ...tempDataArry,
                    {
                        id: data.id,
                        type_leave_name: data.data().type_leave_name
                    }
                ]
            })

            setTypeleave(tempDataArry)
        })


        return () => {
            unsubscribe()

        }
    })
    const userData = (type_leave,id_type_leave,user)=>{ 

         setData({ ...data, type_leave:type_leave, id_type_leave: id_type_leave })
         console.log(user)
        const ref_quota=  firestore.collection("quota").where("uid","==",user).where("type_leave_id","==",id_type_leave);
         ref_quota.onSnapshot( snapshot=>{
            let tempDataArry = []
            snapshot.forEach(data=>{
                tempDataArry=[
                    ...tempDataArry,{
                        type_leave_id:data.data().type_leave_id,
                        amount:data.data().amount
                    }
                ]
                setAmout2(data.data().amount)
            })
             setAmount(tempDataArry)

        })

    }



    const submitHandle=()=>{
        let ref = firestore.collection("leave");

        let obj =
            {uid:user,
            type_leave_id:data.id_type_leave,
            leave_date:new Date(),
            leave_start:data.timestart,
            amount:data.timeend,
            reson:data.reason
            }
        ref.add(obj)
        .then((result)=>{
            const leave_id=result.id
            let ref =firestore.collection("appove");
            let obj={
                leave_id,
                leave_appove:"",
                hr_appove:"",
                leave_remark:"",
                hr_remark:"",
                status:"",
            }

            ref.add(obj).then(()=>{
                console.log("success")
            }).catch(err=>{console.log(err)})
        }).catch(err=>{console.log(err)})
    }
    return (
        <div>
            <div className="dropdown">
                <button onClick={()=>{console.log(amount)}} className="btn btn-sm btn-info dropdown-toggle mr-4" type="button" id="dropdownMenuButton" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false" >
                    ยื่นเรื่องขอลา
                        </button>
                <div className="dropdown-menu" aria-labelledby="dropdownMenuButton">
                    {
                        typeleave.map((data_leave, index) => {
                            return <div className="dropdown-item" onClick={()=> { userData(data_leave.type_leave_name,data_leave.id,user) }} data-toggle="modal" data-target="#staticBackdrop" key={index}>{data_leave.type_leave_name} </div>
                        })
                    }
                </div>
            </div>


            <div className="modal fade" id="staticBackdrop" data-backdrop="static" data-keyboard="false" tabIndex="-1" aria-labelledby="staticBackdropLabel" aria-hidden="true">
                <div className="modal-dialog">
                    <div className="modal-content">
                        <div className="modal-header">
                            <h5 className="modal-title" id="exampleModalLabel">{data.type_leave}</h5>
                            <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                                <span aria-hidden="true">×</span>
                            </button>
                        </div>
                        <div className="modal-body">
                            <div className="container-fluid">
                                <div className="row">
                                    <div className="col-md-6">
                                        <label onClick={()=>{console.log(data)}}>วันที่ลา</label>
                                        <input type="date" className="form-control" onChange={e => { setData({ ...data, timestart: e.target.value }) }} />
                                    </div>
                                    <div className="col-md-6">
                                        <input type="number" className="form-control"  min="0" max={amount2} onChange={e=>{setData({...data,timeend:e.target.value})}}/>
                                        <label htmlFor="">จำนวนการลา</label>

                                    </div>
                                </div>
                                <div className="row">
                                    <div className="col-md-12">
                                        <div className="form-group">
                                            <label htmlFor="">เหตุผล</label>
                                            <textarea className="form-control" onChange={e => { setData({ ...data, reason: e.target.value }) }}></textarea>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                        <div className="modal-footer">
                            <button type="button" className="btn btn-secondary" data-dismiss="modal">Close</button>
                            <button type="button" className="btn btn-primary" data-dismiss="modal" onClick={()=>{submitHandle()}}>Save changes</button>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Form;
