import React,{useEffect,useState} from 'react';
import Footer from '../../component/admin/Footer';
import Navbar from '../../component/admin/Navbar';
import Sidebar from '../../component/user/Sidebar'
import Checkuser from './Checkuser';
import {firestore,auth} from '../../firebase/config'
const Userleave = () => {
    const [user,setUser]=useState([])
    const refAppove = firestore.collection("appove")
    const refLeave = firestore.collection("leave")
    const refUser = firestore.collection("user")
    useEffect(()=>{
      const authUnsubscibe = auth.onAuthStateChanged((firebaseUser)=>{
        if(!!firebaseUser){
            refUser.doc(firebaseUser.uid).onSnapshot(doc=>{
                setUser(doc.data())
            })
        }
    })


    return ()=>{
      authUnsubscibe
    }
    },[])
    return (
      <div>
      <Checkuser />
      <Navbar />
      <Sidebar />
      {/* Content Wrapper. Contains page content */}
      <div className="content-wrapper">
        {/* Content Header (Page header) */}
        <div className="content-header">
          <div className="container-fluid">
            <div className="row mb-2">
            </div>{/* /.row */}
          </div>{/* /.container-fluid */}
        </div>
        {/* /.content-header */}
        {/* Main content */}
        <div className="content">
          <div className="container-fluid">
            <div className="row">
              <div className="col-lg-12">
                <div className="card">
                  <div className="card-header ">
                    <div className="d-flex justify-content-between">
                      <h3 className="m-0 text-dark" onClick={()=>{console.log(user)}} >รายการลาทั้งหมด</h3>

                    </div>
                  </div>
                  <div className="card-body table-responsive ">

                    <table className="table table-hover ">
                      <thead className="thead-dark ">
                        <tr>
                          <th scope="col">วันที่แจ้ง</th>
                          <th scope="col">วันที่ลา</th>
                          <th scope="col">จำนวนวัน</th>
                          <th scope="col">ประเภทการลา</th>
                          <th scope="col">หมายเหตุ</th>
                          <th scope="col">สถานะ</th>
                        </tr>
                      </thead>
                      <tbody>

                      </tbody>
                    </table>
                  </div>
                </div>
              </div>
              {/* /.col-md-6 */}
              {/* /.col-md-6 */}
            </div>
            {/* /.row */}
          </div>
          {/* /.container-fluid */}
        </div>
        {/* /.content */}
      </div>
      {/* /.content-wrapper */}
      {/* Control Sidebar */}
      <aside className="control-sidebar control-sidebar-dark">
        {/* Control sidebar content goes here */}
      </aside>
      {/* /.control-sidebar */}
      <Footer />
    </div >
    );
};

export default Userleave;
