    columns: [
      {
        label: 'ชื่อพนักงาน',
        field: 'name',
        width: 200,
      },
      {
        label: 'วันที่แจ้ง',
        field: 'leave_date',
        width: 200,
      },
      {
        label: 'วันที่ลา',
        field: 'leave_start',
        width: 200,
      },
      {
        label: 'จำนวนวัน',
        field: 'amount',
