        width: 2,
      },
      {
        label: 'ประเภทการลา',
        field: 'type_leave_name',
        sort: 'asc',
        width: 5,
      },
      {
        label: 'เหตุผลที่ลา',
        field: 'reason',
        sort: 'asc',
        width: 5,
      },
      {
        label: 'สถานะ',
        field: 'status',
        sort: 'asc',
        width: 5,
      }
    ]
  });
  useEffect(()=>{
    const usersubscribe =refUser.onSnapshot(doc=>{
      let tempArrayUser = []
      doc.forEach(data=>{
        tempArrayUser=[
          ...tempArrayUser,
          {
            uid:data.id,
            fname:data.data().fname,
            lname:data.data().lname,
            name:data.data().name,
