      let tempArrayAppove=[]
      doc.forEach(data=>{
        tempArrayAppove=[
          ...tempArrayAppove,
          {

          }
        ]
      })
    })

    const leavesubscribe = refLeave.onSnapshot(doc=>{
      let tempArrayLeave=[]
      doc.forEach(data=>{
        tempArrayLeave=[
          ...tempArrayLeave,
          {

          }
        ]
      })
    })


    const typeleavesubscribe =refTypeLeave.onSnapshot(doc=>{
      let tempArrayTypeLeave=[]
      doc.forEach(data=>{
        tempArrayTypeLeave=[
          ...tempArrayTypeLeave,
          {

          }
        ]
      })
    })

    const quotasubscribe =refQuota.onSnapshot(doc=>{
      let tempArrayQuota=[]
      doc.forEach(data=>{
        tempArrayQuota=[
          ...tempArrayQuota,
          {
            quotaId:data.id,
            amount:data.data().amount(),
            type_leave_id:data.data().type_leave_id,
            uid:data.data().uid
          }
        ]
      })
      setQuota(tempArrayQuota)
    })
    return (()=>{
      usersubscribe()
      appovesubscribe()
      leavesubscribe()
      typeleavesubscribe()
      quotasubscribe()
    })

  },[loading])
  return (
    <div>
      <Navbar />
      <Checkadmin />
      <Sidebar />
      {/* Content Wrapper. Contains page content */}
      <div className="content-wrapper">
        {/* Content Header (Page header) */}
