import React, { useEffect, useState } from 'react';
import Footer from '../../component/admin/Footer';
import Navbar from '../../component/admin/Navbar';
import Sidebar from '../../component/user/Sidebar';
import Checkuser from './Checkuser';
import Form from './Userleave/Form';
import { auth, firestore } from '../../firebase/config'
const Userleave = () => {
  const [dataLeaveAppove, setDataLeaveAppove] = useState({
    uid:"",
    amount: "",
    leave_date: "",
    leave_start: "",
    reson: "",
    type_leave_id: "",
    hr_appove: "",
    hr_remark: "",
    leave_appove: "",
    leave_remark: "",
    leave_id: "",
    appove_id: "",
  })

    const test = auth.currentUser
  useEffect(() => {
      console.log(test.id)

    const unSubscribe = auth.onAuthStateChanged((firebase) => {
      if (!!firebase) {
        const ref_leave = firestore.collection("leave").where("uid", "==", firebase.uid)
        ref_leave.onSnapshot(doc_leave => {
          doc_leave.forEach(data_leave => {
            setDataLeaveAppove({
              ...dataLeaveAppove,
              leave_date:data_leave.data().leave_date,
              leave_start:data_leave.data().leave_start,
              reson:data_leave.data().reson,
              type_leave_id:data_leave.data().type_leave_id,
              amount:data_leave.data().amount,
              leave_id:data_leave.id
            })
            const ref_appove = firestore.collection("appove").where("leave_id", "==", data_leave.id)
            ref_appove.onSnapshot(doc_appove => {
              doc_appove.forEach(data_appove => {
                setDataLeaveAppove({
                  ...dataLeaveAppove,uid:firebase.uid,
                  appove:data_appove.data().id,
                  hr_appove:data_appove.data().hr_appove,
                  hr_remark:data_appove.data().hr_appove,
                  leave_appove:data_appove.data().leave_appove,
                  leave_remark:data_appove.data().leave_remark,
                  appove_id:data_appove.id
                })


              })

            })
          })
        })

      }

    })
    return () => {
      // unSubscribe()
    }
  })

  return (
    <div>
      <Checkuser />
      <Navbar />
      <Sidebar />
      {/* Content Wrapper. Contains page content */}
      <div className="content-wrapper">
        {/* Content Header (Page header) */}
        <div className="content-header">
          <div className="container-fluid">
            <div className="row mb-2">
            </div>{/* /.row */}
          </div>{/* /.container-fluid */}
        </div>
        {/* /.content-header */}
        {/* Main content */}
        <div className="content">
          <div className="container-fluid">
            <div className="row">
              <div className="col-lg-12">
                <div className="card">
                  <div className="card-header ">
                    <div className="d-flex justify-content-between">
                      <h3 className="m-0 text-dark">รายการลา</h3>
                      <Form />
                    </div>
                  </div>
                  <div className="card-body table-responsive ">

                    <table className="table table-hover ">
                      <thead className="thead-dark ">
                        <tr>
                          <th scope="col">ชื่อพนักงาน</th>
                          <th scope="col">การลา</th>
                          <th scope="col">วันที่ลา</th>
                          <th scope="col">จำนวนวัน</th>
                          <th scope="col">สถานะ</th>
                          <th scope="col">จัดการ</th>
                        </tr>
                      </thead>
                      <tbody>
                        <tr>
                          <th scope="row">ชนภัทร</th>
                          <td>ลาป่วย</td>
                          <td>03/12/2020</td>
                          <td>5</td>
                          <td>
                            <span className="badge badge-pill badge-dark">รออนุมัติ</span>
                          </td>
                          <td>
                            <button className="btn btn-sm btn-outline-danger">การอนุมัติ</button>
                          </td>
                        </tr>
                        <tr>
                          <th scope="row">ชนภัทร</th>
                          <td>ลากิจ</td>
                          <td>03/12/2020</td>
                          <td>2</td>
                          <td>
                            <span className="badge badge-pill badge-dark">รออนุมัติ</span>
                          </td>
                          <td>
                            <button className="btn btn-sm btn-outline-danger">การอนุมัติ</button>
                          </td>
                        </tr>
                        <tr>
                          <th scope="row">ชนภัทร</th>
                          <td >ลากิจ</td>
                          <td>03/12/2020</td>
                          <td>1</td>
                          <td>
                            <span className="badge badge-pill badge-dark">รออนุมัติ</span>
                          </td>
                          <td>
                            <button className="btn btn-sm btn-outline-danger">การอนุมัติ</button>
                          </td>
                        </tr>
                      </tbody>
                    </table>
                  </div>
                </div>
              </div>
              {/* /.col-md-6 */}
              {/* /.col-md-6 */}
            </div>
            {/* /.row */}
          </div>
          {/* /.container-fluid */}
        </div>
        {/* /.content */}
      </div>
      {/* /.content-wrapper */}
      {/* Control Sidebar */}
      <aside className="control-sidebar control-sidebar-dark">
        {/* Control sidebar content goes here */}
      </aside>
      {/* /.control-sidebar */}
      <Footer />
    </div >
  );
};

export default Userleave;
