import React, { useState, useEffect } from 'react';
import Navbar from '../../component/admin/Navbar'
import Sidebar from '../../component/admin/Sidebar'
import Footer from '../../component/admin/Footer'
import Checkadmin from './Checkadmin';
import { firestore } from '../../firebase/config'
export default function Leavelist() {
  const [loading,setLoading]=useState(true)
  return (
    <div>
       <Navbar />
       <Checkadmin />
       <Sidebar />
       {/* Content Wrapper. Contains page content */}
       <div className="content-wrapper">
         {/* Content Header (Page header) */}
        <div className="content-header">
          <div className="container-fluid">
             <div className="row mb-2">
             </div>{/* /.row */}
           </div>{/* /.container-fluid */}
         </div>
         {/* /.content-header */}
         {/* Main content */}
         <div className="content">
           <div className="container-fluid">
             <div className="row">
               <div className="col-lg-12">
                 <div className="card">
                   <div className="card-header ">
                     <div className="d-flex justify-content-between">
                       <h3 className="m-0 text-dark">รายการลา</h3>
                     </div>
                    </div>
                   <div className="card-body table-responsive ">

                     <table className="table table-hover ">
                       <thead className="thead-dark ">
                         <tr>
                           <th scope="col">ชื่อพนักงาน</th>
                           <th scope="col">ข้อมูลส่วนตัว</th>
                           <th scope="col">วันที่แจ้ง</th>
                           <th scope="col">วันที่ลา</th>
                           <th scope="col">จำนวนวัน</th>
                           <th scope="col">ประเภทการลา</th>
                           <th scope="col">เหตุผล</th>
                           <th scope="col">สถานะ</th>
                           <th scope="col">จัดการ</th>
                         </tr>
                       </thead>
                       {
                         loading ?(
                            <div className="spinner-border mx-auto" style={{width:"3rem",height:"3rem"}} role="status">

                            </div>
                         )
                         : null
                       }
                     </table>
                   </div>
                 </div>
               </div>
               {/* /.col-md-6 */}
               {/* /.col-md-6 */}
             </div>
             {/* /.row */}
           </div>
           {/* /.container-fluid */}
         </div>
        {/* /.content */}
      </div>
       {/* /.content-wrapper */}
       {/* Control Sidebar */}
       <aside className="control-sidebar control-sidebar-dark">
         {/* Control sidebar content goes here */}
       </aside>
       {/* /.control-sidebar */}
       <Footer />


     </div>
  )
}
